mapper.insert:

	SELECT
		CASE
			WHEN
				COUNT(1) > 1
		THEN
			CONCAT('PR',
					LPAD(CAST(REPLACE(MAX(project_code), 'PR', '') AS SIGNED ) + 1,3,'0')
	)
		ELSE
			ifnull (project_code, 'PR001')
		END AS projectCode
	FROM
		project_list

INSERT INTO
	project_list
	(project_code
	,employee_code
	,project_title
	,project_date
	,project_status
	,project_access
	,project_desc
	,project_deadline
	,project_start
	,project_end
	)
VALUES(
	#{projectCode}
	,#{employeeCode}
	,#{projectTitle}
	,now()
	,#{projectStatus}
	,#{projectAccess}
	,#{projectDesc}
	,#{projectDeadline}
	,#{projectStart}
	,#{projectEnd}
	)
